import React, { Component } from 'react';

// creo mi clase para hacer el formulario que registra las tareas
class FormularioTarea extends Component{
    // cosntructor de mi clase
    constructor(){
    super();

    this.state = {
        title: '',
        responsible: '',
        description: '',
        priority: ''
      };
      this.handleInputChange = this.handleInputChange.bind(this);
      this.handleSubmit = this.handleSubmit.bind(this);
}
// Funciones para crear tareas
handleSubmit(e) {
    e.preventDefault();
    this.props.onAddTodo(this.state);
    this.setState({
      title: '',
      responsible: '',
      description: '',
      priority: 'baja'
    });
  }

  handleInputChange(e) {
    const {value, name} = e.target;
    console.log(value, name);
    this.setState({
      [name]: value
    });
  }
render(){
    return(
            // retorno el formulario que generara mis tareas
            <div className="card mt-4 col-md-10">
                <h1 className="text-center">Crea tu Tarea</h1>
            <form onSubmit={this.handleSubmit} className="card-body">
              {/* input para el titulo de la tarea */}
              <div className="form-group">
                <input
                  type="text"
                  name="title"
                  className="form-control"
                  value={this.state.title}
                  onChange={this.handleInputChange}
                  placeholder="Nombre de la Tarea"
                  />
              </div>
              {/* input del responsable*/}
              <div className="form-group">
                <input
                  type="text"
                  name="responsible"
                  className="form-control"
                  value={this.state.responsible}
                  onChange={this.handleInputChange}
                  placeholder="Persona que realiza"
                  />
              </div>
              {/* Input de la descripcion */}
              <div className="form-group">
                <input
                  type="text"
                  name="description"
                  className="form-control"
                  value={this.state.description}
                  onChange={this.handleInputChange}
                  placeholder="Descripcion"
                  />
              </div>
              <div className="form-group">
                <select
                    name="priority"
                    className="form-control"
                    value={this.state.priority}
                    onChange={this.handleInputChange}
                  >
                  
                  <option>baja</option>
                  <option>media</option>
                  <option>alta</option>
                </select>
              </div>
              <button type="submit" className="btn btn-success">
                Guardar
              </button>
            </form>
          </div>
    );
}



}
export default FormularioTarea;
