import React, { Component } from 'react';
import FormularioTarea from './componentes/formtarea';
import Navbar from './componentes/navbar';
import { tareas } from './tareas.json';
class App extends Component {
  constructor() {
    super();
    this.state = {
      tareas
    };
    this.handleAddTarea = this.handleAddTarea.bind(this);
  }
  // metodo para traer mis tareas de el componente  
  handleAddTarea(tarea) {
    this.setState({
      tareas: [...this.state.tareas, tarea]
    })
  }


  render() {
    const tareas = this.state.tareas.map((tarea, i) => {
    return (
      <div className="App">
        <Navbar />
        <FormularioTarea onAddTarea={this.handleAddTarea}/>
      {/* traigo las tareas en una tarjeta*/}
        <div className="col-md-4" key={i}>
          <div className="card mt-4 text-secondary ">
          <div className="card-title text-center">
              {/* titulo de la tarea */}
              <h3>{tarea.title}</h3>
              <span className="badge badge-pill badge-danger ml-2">
                {/* duracion de la tarea */}
                {tarea.priority}
              </span>
            </div>
            <div className="card-body">
              <p className="text-center"> <mark>{tarea.responsible}</mark></p>
              <p className="text-center"> {tarea.description}</p>

            </div>

            <div className="card-footer">



            </div>
          </div>

        </div>


      </div>
    );
    // cierro funcion del map
  });

  }

}

export default App;
