import React, { Component } from 'react';
import FormularioTarea from './componentes/formtarea';
import Navbar from './componentes/navbar';
import { tareas } from './tareas.json';

class App extends Component {
  constructor() {
    super();
    this.state = {
      tareas
    };
    this.handleAddTarea = this.handleAddTarea.bind(this);
  }
  // metodo para traer mis tareas de el componente  
  handleAddTarea(tarea) {
    this.setState({
      tareas: [...this.state.tareas, tarea]
    })
  }


  render() {

 const tareas= this.state.tareas.map((tarea,i) =>{
    return(


      <div className="col-md-4 text-center" key={i}>
        <div className="card mt-4">
        <div className="card-header">
        <h3>{tarea.title}</h3>
        <span className="badge badge-pill badge-danger ml-2">
          {tarea.priority}
        </span>
        </div>
        <div className="card-body">
        <p>{tarea.responsible}</p>
        <p><mark>{tarea.description}</mark></p>
        </div>
        </div>
      </div>


      )
  });



//retorno
    return (
      <div className="App col-md-4">
        <Navbar />
        <FormularioTarea onAddTarea={this.handleAddTarea}/>

        {tareas}

          </div>
    );

  }

}


export default App;
