  constructor(){
    super();
    this.state = {
      tareas
    };
  }
  // metodo para traer mis tareas de el componente  
  handleAddTarea(tarea){
this.state({
 tareas:[...this.state.tareas,tarea]
})
  }


  render(){
    return (
      <div className="App">
        <Navbar/>
        <FormularioTarea/>
      </div>
    );

  }

}

export default App;
