import React, { Component } from 'react';

// creo mi clase para hacer el formulario que registra las tareas
class FormularioTarea extends Component{
    // cosntructor de mi clase
    constructor(){
    super();

    this.state = {
        title: '',
        responsible: '',
        description: '',
        priority: ''
      };
