handreInput(){
console.log('ya te salio putoo ');
}
render(){
    return(
            // retorno el formulario que generara mis tareas
            <div className="card mt-4 col-md-12">
                <h1 className="text-center">Crea tu Tarea</h1>
            <form onSubmit={this.handleSubmit} className="card-body">
              {/* input para el titulo de la tarea */}
              <div className="form-group">
                <input
                  type="text"
                  name="title"
                  className="form-control"
                  onChange={this.handleInput}
                  placeholder="Nombre de la Tarea"
                  />
              </div>
              {/* input del responsable*/}
              <div className="form-group">
                <input
                  type="text"
                  name="responsible"
                  className="form-control"
                
                  onChange={this.handleInput}
                  placeholder="Persona que realiza"
                  />
              </div>
              {/* Input de la descripcion */}
              <div className="form-group">
                <input
                  type="text"
                  name="description"
                  className="form-control"
                 
                  onChange={this.handleInput}
                  placeholder="Descripcion"
                  />
              </div>
              <div className="form-group">
                <select
                    name="priority"
                    className="form-control"
                
                    onChange={this.handleInput}
                  >
                  
                  <option>baja</option>
                  <option>media</option>
                  <option>alta</option>
                </select>
              </div>
              <button type="submit" className="btn btn-success">
                Guardar
              </button>
            </form>
          </div>
    );
}



}
export default FormularioTarea;
